import React, { useState } from "react";

const TriviaAnswer = ({
    number,
    setQuestionNumber,
    setStop,
    incorrect,
    correct
}) => {


    // const [selectedAnswer, setSelectedAnswer] = useState(null);

    const [styleWrong, setStyleWrong] = useState("answer")
    const [styleWrong1, setStyleWrong1] = useState("answer")
    const [styleWrong2, setStyleWrong2] = useState("answer")
    const [styleCorrect, setStyleCorrect] = useState("answer");

    // const delay = ( duration, callback ) => {
    //     setTimeout(() => {
    //         callback();
    //     }, duration);
    // };



    console.log("dolazi broj", number)

    const handleCorrect = () => {
        console.log("klik handleCorrect");
        setStyleCorrect("answer active");
       setTimeout(() => {setStyleCorrect("answer correct")}, 1000);
       setTimeout(() => {setQuestionNumber((prev) => prev + 1)}, 5000);
       setTimeout(() => {setStyleCorrect("answer")}, 5000);

        // delay(1000, () => setStyleCorrect("answer correct"));
        // delay(6000, () => setQuestionNumber((prev) => prev + 1));
        // delay(6000, () => setStyleCorrect("answer"));
    };

    // const handleWrong = () => {
    //     console.log("klik na handleWrong");

    //     setStyleWrong("answer active");
    //     delay(1000, () => setStyleWrong("answer wrong"));

     
    //     delay(6000, setStop(true));

    // };

    // const handleWrong1 = () => {
    //     console.log("klik na handleWrong");
    //     setStyleWrong1("answer active");
    //     delay(1000, () => setStyleWrong1("answer wrong"));
     
    //     delay(6000, () => setStop(true));
    // };

    // const handleWrong2 = () => {
    //     console.log("klik na handleWrong");
    //     setStyleWrong2("answer active");
    //     delay(1000, () => setStyleWrong2("answer wrong"));
     
    //     delay(6000, () => setStop(true));
    // };

    const handleWrong = () => {
        setStyleWrong("answer active");
       setTimeout(() => {setStyleWrong("answer wrong")}, 1000);
       setTimeout(() => {setStop(true)}, 3000);
    };

    const handleWrong1 = () => {
        setStyleWrong1("answer active");
       setTimeout(() => {setStyleWrong1("answer wrong")}, 1000);
       setTimeout(() => {setStop(true)}, 3000);
    };

    const handleWrong2 = () => {
        setStyleWrong2("answer active");
       setTimeout(() => {setStyleWrong2("answer wrong")}, 1000);
       setTimeout(() => {setStop(true)}, 3000);
    };




    if (number == 1) {
        return (
            <>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{correct}</div>
                <div className={styleWrong}

                    onClick={handleWrong}>{incorrect?.[0]}</div>
                <div className={styleWrong}
                    onClick={handleWrong1}>{incorrect?.[1]}</div>
                <div className={styleWrong}
                    onClick={handleWrong2}>{incorrect?.[2]}</div>
            </>
        )

    } else if (number == 2) {
        return (
            <>
                <div className={styleWrong}
                    onClick={handleWrong}>{incorrect?.[0]}</div>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{correct}</div>
                <div className={styleWrong1}
                    onClick={handleWrong1}>{incorrect?.[1]}</div>
                <div className={styleWrong2}
                    onClick={handleWrong2}>{incorrect?.[2]}</div>
            </>
        )

    } else if (number == 3) {
        return (
            <>
                <div className={styleWrong}
                    onClick={handleWrong}>{incorrect?.[0]}</div>
                <div className={styleWrong1}
                    onClick={handleWrong1}>{incorrect?.[1]}</div>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{correct}</div>
                <div className={styleWrong2}
                    onClick={handleWrong2}>{incorrect?.[2]}</div>

            </>
        )
    } else if (number == 0) {
        return (
            <>
                <div className={styleWrong}
                    onClick={handleWrong}>{incorrect?.[0]}</div>
                <div className={styleWrong1}
                    onClick={handleWrong1}>{incorrect?.[1]}</div>
                <div className={styleWrong2}
                    onClick={handleWrong2}>{incorrect?.[2]}</div>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{correct}</div>
            </>
        )
    }

}
export default TriviaAnswer;
