import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";




const Home = () => {

    const [category, setCategory] = useState([]);
    const navigate = useNavigate();


    useEffect(() => {
        axios.get(`./trivia.json`).then(res => {
            const data = res.data;
            setCategory(data)
            console.log("trivia kategorije podaci", data)
        });
    }, []);

    const handleEasy = () => {
        const LinkTo = '/easy';
        navigate(LinkTo);
    }

    const handleMedium = () => {
        const LinkTo = '/medium';
        navigate(LinkTo);
    }

    const handleHard = () => {
        const LinkTo = '/hard';
        navigate(LinkTo);
    }

    const handleOption = (event) => {
        console.log("klik sa option linka", event.target.value);
        const LinkTo = `/option/${event.target.value}`;
        navigate(LinkTo);
    }

    const handleOption2 = (event) => {
        console.log("klik sa option linka", event.target.value);
        const LinkTo = `/option2/${event.target.value}`;
        navigate(LinkTo);
    }

    const handleOption3 = (event) => {
        console.log("klik sa option linka", event.target.value);
        const LinkTo = `/option3/${event.target.value}`;
        navigate(LinkTo);
    }

    return (
        <>
            <div className="home">
                <div className="title">Welcome<br></br>
                    Test your general knowledge,<br></br>
                    you have 45 seconds for each answer.<br></br>
                    Choose the difficulty of random questions. </div>
                <div className="buttons" >
                    <div className="button"
                        onClick={() => handleEasy()}>
                        Easy</div>
                    <div className="button"
                        onClick={() => handleMedium()}>
                        Medium</div>
                    <div className="button"
                        onClick={() => handleHard()}>
                        Hard</div>
                </div>
                <div className="buttons">
                <select onChange={handleOption} className="select">
                    {category.map((categ) => (
                        <option key={categ.id} value={categ.numb}>
                            {categ.title}
                        </option>
                    ))}
                </select>
                <select onChange={handleOption2} className="select">
                    {category.map((categ) => (
                        <option key={categ.id} value={categ.numb}>
                            {categ.title}
                        </option>
                    ))}
                </select>
                <select onChange={handleOption3} className="select">
                    {category.map((categ) => (
                        <option key={categ.id} value={categ.numb}>
                            {categ.title}
                        </option>
                    ))}
                </select>
                </div>
            </div>
        </>
    )

}
export default Home;
