import React, { useState } from "react";

const TriviaAnswer = (props) => {
    // const number = Math.floor(Math.random() * 3);
    // console.log("random broj", number)


    // const [style, setStyle] = useState("close");
    // const [style1, setStyle1] = useState("close");
    // const [style2, setStyle2] = useState("close");

    // const [styleCo, setStyleCo] = useState("closeCo");


    console.log("dolazi broj", props.number)

    // const changeStyle = () => {
    //     console.log("klik na promasaj");
    //     if (style !== "close") setStyle("close");
    //     else setStyle("open");
    // }
    // const changeStyle1 = () => {
    //     console.log("klik na promasaj");
    //     if (style1 !== "close") setStyle1("close");
    //     else setStyle1("open");
    // }
    // const changeStyle2 = () => {
    //     console.log("klik na promasaj");
    //     if (style2 !== "close") setStyle2("close");
    //     else setStyle2("open");
    // }
    // const changeStyleCo = () => {
    //     console.log("klik na pogodak");
    //     if (styleCo !== "closeCo") setStyleCo("closeCo");
    //     else setStyleCo("openCo");
    // }

    if (props.number == 1) {
        return (
            <>
                <div className="answer">{props.correct}</div>
                <div className="answer">{props.incorrect?.[0]}</div>
                <div className="answer">{props.incorrect?.[1]}</div>
                <div className="answer">{props.incorrect?.[2]}</div>
            </>
        )

    } else if (props.number == 2) {
        return (
            <>
                <div className="answer">{props.incorrect?.[0]}</div>
                <div className="answer">{props.correct}</div>
                <div className="answer">{props.incorrect?.[1]}</div>
                <div className="answer">{props.incorrect?.[2]}</div>
            </>
        )

    } else if (props.number == 3) {
        return (
            <>
                <div className="answer">{props.incorrect?.[0]}</div>
                <div className="answer">{props.incorrect?.[1]}</div>
                <div className="answer">{props.correct}</div>
                <div className="answer">{props.incorrect?.[2]}</div>

            </>
        )
    } else if (props.number == 0) {
        return (
            <>
                <div className="answer">{props.incorrect?.[0]}</div>
                <div className="answer">{props.incorrect?.[1]}</div>
                <div className="answer">{props.incorrect?.[2]}</div>
                <div className="answer">{props.correct}</div>
            </>
        )
    }

}
export default TriviaAnswer;
