import React, { useState } from "react";

const TriviaAnswer = (props) => {


    const [selectedAnswer, setSelectedAnswer] = useState(null);
    const [className, setClassName] = useState("answer");
    const [styleWrong, setStyleWrong] = useState("answer")
    const [styleWrong1, setStyleWrong1] = useState("answer")
    const [styleWrong2, setStyleWrong2] = useState("answer")
    const [styleCorrect, setStyleCorrect] = useState("answer");

    const delay = (duration, callback) => {
setTimeout(() => {
    callback();
}, duration)
    };



    console.log("dolazi broj", props.number)

    const handleCorrect = () => {
        console.log("klik handleCorrect");
        // if (styleCorrect !== "answer") setStyleCorrect("answer");
        // else setStyleCorrect("answer correct");
        setStyleCorrect("answer active")
        delay(3000, () => setStyleCorrect("answer correct"));
        delay(6000, () => props.setQuestionNumber((prev) => prev + 1)); 
        delay(6000, () => setStyleCorrect("answer")); 
    };

    const handleWrong = () => {
        console.log("klik na handleWrong");
        // if (styleWrong !== "answer") setStyleWrong("answer");
        // else setStyleWrong("answer wrong");
        setStyleWrong("answer active")
        setTimeout(() => {
setStyleWrong("answer wrong")
        }, 2000);
    };

    const handleWrong1 = () => {
        console.log("klik na handleWrong");
        // if (styleWrong1 !== "answer") setStyleWrong("answer");
        // else setStyleWrong1("answer wrong");
        setStyleWrong1("answer active")
        setTimeout(() => {
setStyleWrong1("answer wrong")
        }, 2000);
    };

    const handleWrong2 = () => {
        console.log("klik na handleWrong");
        // if (styleWrong2 !== "answer") setStyleWrong("answer");
        // else setStyleWrong2("answer wrong");
        setStyleWrong2("answer active")
        setTimeout(() => {
setStyleWrong2("answer wrong")
        }, 2000);
    };


    if (props.number == 1) {
        return (
            <>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{props.correct}</div>
                <div className={styleWrong}
                
                onClick={handleWrong}>{props.incorrect?.[0]}</div>
                <div className={styleWrong}
                onClick={handleWrong1}>{props.incorrect?.[1]}</div>
                <div className={styleWrong}
                onClick={handleWrong2}>{props.incorrect?.[2]}</div>
            </>
        )

    } else if (props.number == 2) {
        return (
            <>
                <div className={styleWrong}
                onClick={handleWrong}>{props.incorrect?.[0]}</div>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{props.correct}</div>
                <div className={styleWrong1}
                onClick={handleWrong1}>{props.incorrect?.[1]}</div>
                <div className={styleWrong2}
                onClick={handleWrong2}>{props.incorrect?.[2]}</div>
            </>
        )

    } else if (props.number == 3) {
        return (
            <>
                <div className={styleWrong}
                onClick={handleWrong}>{props.incorrect?.[0]}</div>
                <div className={styleWrong1}
                onClick={handleWrong1}>{props.incorrect?.[1]}</div>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{props.correct}</div>
                <div className={styleWrong2}
                onClick={handleWrong2}>{props.incorrect?.[2]}</div>

            </>
        )
    } else if (props.number == 0) {
        return (
            <>
                <div className={styleWrong}
                onClick={handleWrong}>{props.incorrect?.[0]}</div>
                <div className={styleWrong1}
                onClick={handleWrong1}>{props.incorrect?.[1]}</div>
                <div className={styleWrong2}
                onClick={handleWrong2}>{props.incorrect?.[2]}</div>
                <div className={styleCorrect}
                    onClick={handleCorrect}>{props.correct}</div>
            </>
        )
    }

}
export default TriviaAnswer;
